  ) {}

  /**
   * Retrieves ticket details by ticket ID.
   * @param ticketId The ID of the ticket.
   * @returns An Observable of GetTicketDetailsResponse.
   */
  getTicketById(ticketId: string): Observable<GetTicketDetailsResponse> {
